action.param:
"function fixScreen() {
    document.getElementById('c2canvas').style.width = '512px';
    document.getElementById('c2canvas').style.height = '384px';
    document.getElementById('c2canvas').width = '640';
    document.getElementById('c2canvas').height = '480';
    document.getElementById('c2canvasdiv').style.width = '512px';
    document.getElementById('c2canvasdiv').style.height = '384px';
    document.getElementById('c2canvasdiv').style.marginLeft = '62px';
    document.getElementById('c2canvasdiv').style.marginTop = '34px';
}

function toggleFullScreen() {
    if (!document.fullscreenElement) {  // current working methods
      if (document.documentElement.requestFullscreen) {
        document.documentElement.requestFullscreen();
        document.getElementById('c2canvas').width = '800';
        document.getElementById('c2canvas').height = '600';
        document.getElementById('c2canvasdiv').style.width = '640px';
        document.getElementById('c2canvasdiv').style.height = '480px';
        document.getElementById('c2canvasdiv').style.marginLeft = '-2px';
        document.getElementById('c2canvasdiv').style.marginTop = '-14px';
      }
    } else {
       if (document.cancelFullScreen) {
          document.cancelFullScreen();
          setTimeout(fixScreen, 500);
       } else if (document.webkitCancelFullScreen) {
         document.webkitCancelFullScreen();
         setTimeout(fixScreen, 100);
       }
    }
  }

  document.addEventListener('keydown', (e) => {         
    if (e.key === 'F4') {
      toggleFullScreen();
    }
  });"
